import Header from "./components/Header/Header";
import UserInput from "./components/UserInput/UserInput";
import ResultsTable from "./components/ResultsTable/ResultTable";
import { useState } from "react";

function App() {
  const [result, setResult] = useState(null);

  const calculateHandler = (userInput) => {
    setResult(userInput);
  };
  const yearlyData = [];

  if (result) {
    let currentSavings = +result['current-savings'];
    const yearlyContribution = +result['yearly-contribution'];
    const expectedReturn = +result['expected-return'] / 100;
    const duration = +result['duration'];

    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
  }
  setResult(yearlyData);

  return (
    <div>




      {/* Todo: Show below table conditionally (only once result data is available) */}
      {/* Show fallback text if no data is available */}
      <Header></Header>
      <UserInput onCalculate={calculateHandler}></UserInput>
      {!userInput && <p>No investment calculated yet.</p>}
      {userInput && <ResultsTable></ResultsTable>}

    </div>
  );
}

export default App;
