import React,{useState} from "react";

const initialUserInput  = {
  currentSavings: 10000,
  yearlyContribution : 1200,
  expectedReturn:7,
  duration: 10,
};

const UserInput = () => {
  const[userInput,setUserInput] = useState(initialUserInput);

  const submitHandler = (event) => {
      event.preventDefault()
      console.log('Submit');
    }
    const resetHandler = () => {
      setUserInput(initialUserInput);
    }
    const inputChangeHandler = (input,value) => {
      console.log(input,value);
      setUserInput((prevInput) =>{
        return{
          ...prevInput,
          [input] : value,
        };
      });
    }
    return(
        <form className="form" onSubmit={submitHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input onChange={(event) => inputChangeHandler('current-savings', event.target.value)} type="number" id="current-savings" value={userInput.currentSavings} />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input onChange={(event) => inputChangeHandler('yearly-contribution', event.target.value)} type="number" id="yearly-contribution" value={userInput.yearlyContribution} />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input onChange={(event) => inputChangeHandler('expected-return', event.target.value)} type="number" id="expected-return" value={userInput.expectedReturn} />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input onChange={(event) => inputChangeHandler('duration', event.target.value)} type="number" id="duration" value={userInput.duration}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt"onClick={resetHandler}>
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    );
}
export default UserInput;
